/**
 * Import AddMovieForm module css
 */

import { nanoid } from "nanoid";
import { useState } from "react";
import styles from "./AddMovieForm.module.css";

// membuat komponen add movie form

const AddMovieForm = (props) => {
  // melakukan props dari home.js
  const { movies, setMovies } = props;

  // melakukan state pada form input title, date dan genre
  const [title, setTitle] = useState("");
  const [date, setDate] = useState("");
  const [genre, setGenre] = useState("");

  // handle input title
  const handleTitle = (e) => {
    setTitle(e.target.value);
  };

  // handle input year
  const handleDate = (e) => {
    setDate(e.target.value);
  };

  // handle select genre
  const handleGenre = (e) => {
    setGenre(e.target.value);
  };

  // handle form
  const handleSubmit = (e) => {
    //melakukan default agar form tidak tersubmit
    e.preventDefault();

    const movie = {
      id: nanoid(),
      title: title,
      year: date,
      type: genre,
      poster: "https://m.media-amazon.com/images/I/81GBxNO9swL._AC_SY679_.jpg",
    };

    // melakukan push data ke setmovies
    setMovies([...movies, movie]);
  };

  return (
    <div className={styles.container}>
      <section className={styles.addmovieform}>
        <div className={styles.addmovieform__left}>
          <img
            className={styles.addmovieform__image}
            src="https://picsum.photos/330/330"
            alt="placeholder"
          />

          im
        </div>

        <div className={styles.addmovieform__right}>
          <div>
            <form
              onSubmit={handleSubmit}
              className={styles.addmovieform__form}
              action=""
            >
              <div className={styles.addmovieform__formControl}>
                <label
                  htmlFor="title"
                  className={styles.addmovieform__labelTitle}
                >
                  Add Title
                </label>
                <input
                  onChange={handleTitle}
                  type="text"
                  className={styles.addmovieform__input}
                  value={title}
                />
              </div>
              <div className={styles.addmovieform__formControl}>
                <label
                  htmlFor="year"
                  className={styles.addmovieform__labelYear}
                >
                  Add Year
                </label>
                <input
                  onChange={handleDate}
                  type="text"
                  value={date}
                  className={styles.addmovieform__input}
                />
              </div>
              <div className={styles.addmovieform__formControl}>
                <label
                  htmlFor="type"
                  className={styles.addmovieform__labelType}
                >
                  Pilih Genre
                </label>
                <select
                  name="type"
                  id="type"
                  value={genre}
                  onChange={handleGenre}
                  className={styles.addmovieform__selectType}
                >
                  <option value="Horror">Horror</option>
                  <option value="Action">Action</option>
                  <option value="Thriller">Thriller</option>
                  <option value="Drama">Drama</option>
                  <option value="Survival">Survival</option>
                </select>
              </div>
              <div className={styles.addmovieform__formButton}>
                <button className={styles.addmovieform__button}>Submit</button>
              </div>
            </form>
          </div>
        </div>
      </section>
    </div>
  );
};

export default AddMovieForm;
